import{_ as p,c as e}from"./app.fc0f621f.js";const t={},a=e('<h1 id="\u5F53\u5B69\u5B50\u8981\u6C42-\u62B1\u62B1-\u65F6-\u4ED6\u5B9E\u9645\u662F\u5728\u9700\u8981\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u5F53\u5B69\u5B50\u8981\u6C42-\u62B1\u62B1-\u65F6-\u4ED6\u5B9E\u9645\u662F\u5728\u9700\u8981\u4EC0\u4E48" aria-hidden="true">#</a> \u5F53\u5B69\u5B50\u8981\u6C42\u201C\u62B1\u62B1\u201D\u65F6\uFF0C\u4ED6\u5B9E\u9645\u662F\u5728\u9700\u8981\u4EC0\u4E48\uFF1F</h1><p>\u5BF9\u4E8E\u8BDD\u80FD\u8BF4\u5229\u7D22\u4E4B\u540E\u7684\u5C0F\u5A03\uFF0C</p><p>\u6492\u5A07\u7684\u65F6\u5019\u4F1A\u8BF4\uFF0C\u62B1\u62B1\u6211~</p><p>\u9AD8\u51B7\u7684\u65F6\u5019\u4F1A\u8BF4\uFF0C\u62B1\u6211\uFF01</p><p>\u53D1\u813E\u6C14\u7684\u65F6\u5019\u4F1A\u8BF4\uFF0C\u62B1\uFF01\u62B1\uFF01\u6211\uFF01</p><p>\u201C\u5988\u5988\uFF0C\u62B1\u62B1\u6211\uFF01\u201D\u5982\u679C\u8FD9\u53E5\u8BDD\u7684\u91CD\u590D\u7387\u6392\u7B2C\u4E8C\uFF0C\u6CA1\u4EBA\u6562\u6392\u7B2C\u4E00\uFF01</p><p>\u4E3A\u4EC0\u4E48\u5B69\u5B50\u90A3\u4E48\u9700\u8981\u62B1\uFF1F</p><p>\u4E0D\u4F1A\u8D70\u7684\u65F6\u5019\u8981\u62B1\uFF0C\u4F1A\u8DD1\u4E86\u8FD8\u8981\u62B1\uFF1B\u9AD8\u5174\u7684\u65F6\u5019\u8BA9\u4F60\u62B1\uFF0C\u751F\u6C14\u4E86\u8FD8\u8BA9\u4F60\u62B1\uFF01</p><p>\u62E5\u62B1\uFF0C\u6709\u4E00\u79CD\u201C\u8FF7\u4E4B\u795E\u79D8\u201D\u7684\u529B\u91CF\uFF0C\u4E0D\u4EC5\u80FD\u6EE1\u8DB3\u4EBA\u7C7B\u7684\u751F\u7406\u9700\u8981\uFF0C\u8FD8\u80FD\u586B\u8865\u4EBA\u7C7B\u7684\u7CBE\u795E\u9700\u6C42\u3002</p><p>\u4ECA\u5929\u6211\u4EEC\u5C31\u6765\u804A\u4E00\u804A\u62E5\u62B1\u7684\u529B\u91CF\u3002</p><p>01</p><p>\u9700\u8981\u62E5\u62B1\u662F\u56E0\u4E3A\u76AE\u80A4\u9965\u997F</p><p>\u808C\u80A4\u4E4B\u4EB2\uFF0C\u5176\u5B9E\u662F\u4EBA\u7C7B\u7684\u4E00\u79CD\u57FA\u672C\u9700\u6C42\u3002</p><p>\u4E0A\u4E2A\u4E16\u7EAA50\u5E74\u4EE3\uFF0C\u4E00\u4F4D\u540D\u53EB\u54C8\u6D1B\u7684\u7F8E\u56FD\u5FC3\u7406\u5B66\u5BB6\uFF0C\u505A\u4E86\u4E00\u7CFB\u5217\u7334\u5B50\u548C\u6BCD\u4EB2\u5173\u7CFB\u7684\u5B9E\u9A8C\uFF0C\u8BC1\u660E\u52A8\u7269\u4E0E\u4EBA\u90FD\u5B58\u5728\u7740\u76AE\u80A4\u9965\u997F\u7684\u95EE\u9898\u3002</p><p>\u800C\u8FD9\u4E2A\u95EE\u9898\u5728\u5A74\u5E7C\u513F\u8EAB\u4E0A\u5C24\u4E3A\u7A81\u51FA\u3002</p><p>\u5B9D\u5B9D\u5929\u751F\u6E34\u671B\u5F97\u5230\u5927\u4EBA\u7684\u6402\u62B1\uFF0C\u5373\u4FBF\u957F\u5927\u4E5F\u4E60\u60EF\u9760\u7740\u5927\u4EBA\u3001\u6293\u7740\u5927\u4EBA\u7684\u80F3\u818A\uFF0C\u8FD9\u5C31\u662F\u76AE\u80A4\u9965\u997F\u7684\u8868\u73B0\u3002</p><p>\u6D88\u9664\u76AE\u80A4\u9965\u997F\u7684\u6700\u597D\u98DF\u54C1\uFF0C\u5C31\u662F\u7238\u7238\u5988\u5988\u7684\u4EB2\u543B\u3001\u629A\u6469\u3001\u62E5\u62B1\u7B49\u808C\u80A4\u4E4B\u4EB2\u3002</p><p>\u5982\u679C\u5B9D\u5B9D\u7684\u76AE\u80A4\u957F\u671F\u5904\u4E8E\u9965\u997F\u72B6\u6001\uFF0C\u6781\u5C11\u5F97\u5230\u7236\u6BCD\u62E5\u62B1\uFF0C\u5C31\u5BB9\u6613\u5F15\u53D1\u6027\u60C5\u5B64\u50FB\u3001\u7231\u54AC\u5634\u5507\u6216\u5543\u6307\u7532\uFF0C\u957F\u5927\u540E\u5BB9\u6613\u5F62\u6210\u7684\u4E00\u79CD\u6F5C\u5728\u7684\uFF0C\u5BF9\u88AB\u7231\u3001\u88AB\u5173\u5FC3\u3001\u88AB\u629A\u6170\u7684\u6E34\u671B\u3002</p><p>\u5F53\u8FD9\u79CD\u611F\u89C9\u8FC7\u4E8E\u5F3A\u70C8\u65F6\uFF0C\u53EF\u80FD\u5C31\u4F1A\u5BFC\u81F4\u4E00\u79CD\u75C5\u6001\u7684\u60C5\u611F\u9700\u6C42\u3002</p><p>\u8FD1\u5E74\u6765\uFF0C\u8BB8\u591A\u7814\u7A76\u5F97\u5230\u76F8\u540C\u7684\u7ED3\u8BBA\uFF1A\u5E38\u88AB\u4EBA\u63E1\u7740\u3001\u62E5\u62B1\u548C\u4EB2\u543B\u7684\u5A74\u5B69\uFF0C\u6BD4\u90A3\u4E9B\u88AB\u4EBA\u957F\u671F\u7529\u5728\u4E00\u8FB9\u4E14\u65E0\u4EBA\u78B0\u89E6\u7684\u5B69\u5B50\u66F4\u5BB9\u6613\u53D1\u5C55\u51FA\u5065\u5168\u7684\u611F\u60C5\u751F\u6D3B\u3002</p><p>02</p><p>\u62E5\u62B1\u624D\u80FD\u611F\u53D7\u201C\u6211\u7231\u4F60\u201D\u7684\u4E8B\u5B9E</p><p>\u6211\u4EEC\u4E00\u76F4\u90FD\u89C9\u5F97\uFF0C\u5BF9\u5B69\u5B50\u8868\u8FBE\u7231\u548C\u5173\u5FC3\u7684\u65B9\u5F0F\u5C31\u662F\u8BED\u8A00\u3002</p><p>\u5176\u5B9E\uFF0C\u5373\u4FBF\u4E0D\u8BF4\u8BDD\uFF0C\u901A\u8FC7\u80A2\u4F53\u8BED\u8A00\u4E5F\u80FD\u4F20\u9012\u6211\u4EEC\u5185\u5FC3\u7684\u771F\u5B9E\u60F3\u6CD5\u3002</p><p>\u5BF9\u4E8E\u5E74\u5E7C\u7684\u5B69\u5B50\u6765\u8BB2\uFF0C\u80A2\u4F53\u8BED\u8A00\u5F80\u5F80\u6BD4\u8BED\u8A00\u66F4\u6709\u529B\u91CF\u3002</p><p>\u7F8E\u56FD\u8BED\u8A00\u5B66\u5BB6\u827E\u4F2F\u7279\xB7\u6885\u745E\u5BBE\u901A\u8FC7\u7814\u7A76\u53D1\u73B0\uFF1A\u4EBA\u4E0E\u4EBA\u4E4B\u95F4\u7684\u6C9F\u901A\uFF0C\u53EA\u67097%\u662F\u901A\u8FC7\u8BED\u8A00\u6C9F\u901A\u7684\uFF0C\u9AD8\u8FBE93%\u7684\u6C9F\u901A\u662F\u901A\u8FC7\u975E\u8BED\u8A00\u6C9F\u901A\u8FDB\u884C\u7684\u3002</p><p>\u6C9F\u901A\u7684\u603B\u6548\u679C=7%\u7684\u8BED\u8A00+38%\u7684\u97F3\u8C03+55%\u7684\u80A2\u4F53\u8BED\u8A00\u3002</p><p>\u8FD9\u4E2A\u7EC4\u5408\u6A21\u5F0F\u7B80\u76F4\u4E0D\u8981\u66F4\u9002\u5408\u54B1\u4EEC\u8BDD\u8FD8\u8BF4\u4E0D\u5229\u7D22\u7684\u5C0F\u5B9D\u5B9D\u597D\u4E0D\uFF1F</p><p>\u8C08\u604B\u7231\u7684\u65F6\u5019\u8001\u5988\u5B50\u4EEC\u90FD\u77E5\u9053\uFF0C\u4E00\u5343\u53E5\u6211\u7231\u4F60\uFF0C\u4E5F\u62B5\u4E0D\u8FC7\u4E00\u4E2A\u6CD5\u5F0F\u957F\u543B\u6765\u7684\u771F\u5B9E\uFF0C\u56E0\u4E3A\u5634\u5DF4\u53EF\u4EE5\u8BF4\u8C0E\uFF0C\u4F46\u8EAB\u4F53\u4E0D\u884C\u3002</p><p>\u4EB2\u5B50\u5173\u7CFB\u4E5F\u4E00\u6837\u554A\uFF0C\u5F88\u591A\u4EBA\u90FD\u89C9\u5F97\u4ED8\u51FA\u4E86\u5F88\u591A\uFF0C\u5B69\u5B50\u597D\u50CF\u4E0D\u9886\u60C5\uFF0C\u5982\u679C\u5B69\u5B50\u8DDF\u4F60\u4E0D\u4EB2\uFF0C\u4E00\u5B9A\u662F\u4F60\u6CA1\u505A\u597D\u201C\u62E5\u62B1\u201D\u8FD9\u4EF6\u5C0F\u4E8B\u3002</p><p>\u6240\u6709\u7684\u5B69\u5B50\u90FD\u9700\u8981\u6210\u4EBA\u7684\u62E5\u62B1\u4E0E\u629A\u6478\uFF0C\u624D\u4F1A\u611F\u53D7\u5230\u201C\u4F60\u7231\u4ED6\u201D\u7684\u4E8B\u5B9E\u3002</p><p>\u80A2\u4F53\u63A5\u89E6\u6BD4\u5982\u8BF4\u201C\u6211\u7231\u4F60\u201D\u3001\u9001\u793C\u7269\u3001\u957F\u60C5\u7684\u966A\u4F34\uFF0C\u66F4\u80FD\u4F20\u8FBE\u7231\u610F\u4E14\u66F4\u6DF1\u5165\u3002</p><p>\u8BF4\u4E00\u5343\u904D\u6211\u7231\u4F60\uFF0C\u5374\u4ECE\u4E0D\u62E5\u62B1\u5979\uFF0C\u4F60\u8FD8\u80FD\u6709\u70B9\u771F\u8BDA\u4E0D\uFF1F</p><p>03</p><p>\u62E5\u62B1\u80FD\u9547\u5B9A\u5B69\u5B50\u7684\u60C5\u7EEA</p><p>\u7F8E\u56FD\u4E00\u4F4D\u5A74\u513F\u5FC3\u7406\u5B66\u5BB6\u8BF4\u9053\uFF1A</p><p>\u5F53\u5B9D\u5B9D\u88AB\u6BCD\u4EB2\u62B1\u5728\u80F8\u524D\u65F6\uFF0C\u6BCD\u5A74\u4E4B\u95F4\u5C31\u5EFA\u7ACB\u8D77\u4E00\u79CD\u6700\u5177\u5B89\u629A\u529F\u80FD\u7684\u63A5\u89E6\u2014\u2014\u80F8\u5BF9\u80F8\u63A5\u89E6\uFF0C\u5B9D\u5B9D\u4E0D\u9002\u7684\u5FC3\u5883\u4F1A\u7ACB\u5373\u5F97\u5230\u6BCD\u7231\u7684\u6700\u4F73\u629A\u6170\u800C\u8D8B\u4E8E\u5E73\u9759\uFF0C\u60C5\u7EEA\u9700\u6C42\u4FBF\u5F97\u4EE5\u6EE1\u8DB3\u3002</p><p>\u5982\u679C\u5988\u5988\u80FD\u5728\u7B2C\u4E00\u65F6\u95F4\u5C06\u521D\u751F\u5A74\u513F\u62E5\u5165\u6000\u91CC\uFF0C\u80FD\u964D\u4F4E\u4ED6\u521D\u6765\u8FD9\u4E2A\u964C\u751F\u4E16\u754C\u7684\u4E0D\u5B89\u4E0E\u5BB3\u6015\u3002</p><p>\u5BF9\u4E8E\u5927\u4E00\u4E9B\u7684\u5B9D\u5B9D\uFF0C\u62E5\u62B1\u5B89\u629A\u60C5\u7EEA\u7684\u529B\u91CF\u4E5F\u4E00\u6837\u91CD\u8981\u3002</p><p>\u901A\u5E38\u5B9D\u5B9D\u60C5\u7EEA\u6FC0\u52A8\u65F6\uFF0C\u7236\u6BCD\u96BE\u514D\u4F1A\u4EA7\u751F\u538C\u70E6\u5FC3\u7406\u3002</p><p>\u5F88\u591A\u65F6\u5019\u4F1A\u9009\u62E9\u51B7\u843D\u5B69\u5B50\uFF0C\u6839\u672C\u4E0D\u613F\u610F\u62E5\u62B1\u5B69\u5B50\uFF0C\u751A\u81F3\u5F88\u53EF\u80FD\u4F1A\u62D2\u7EDD\u5B69\u5B50\u548C\u81EA\u5DF1\u7684\u78B0\u89E6\u3002</p><p>\u5176\u5B9E\uFF0C\u8FD9\u4E2A\u65F6\u5019\uFF0C\u5B69\u5B50\u66F4\u9700\u8981\u7236\u6BCD\u7684\u62E5\u62B1\uFF0C\u8FD9\u4F1A\u8FC5\u901F\u5B89\u629A\u5B69\u5B50\u7684\u60C5\u7EEA\uFF0C\u8BA9\u5B69\u5B50\u5F88\u6709\u5B89\u5168\u611F\u3002</p><p>\u6700\u521D\u4E0E\u5B69\u5B50\u4EA7\u751F\u51B2\u7A81\u7684\u65F6\u5019\uFF0C\u6211\u4E5F\u91C7\u53D6\u4FDD\u6301\u8DDD\u79BB\u7684\u65B9\u5F0F\uFF0C\u5979\u5C31\u4F1A\u8FB9\u54ED\u8FB9\u8BF4\uFF0C\u4F60\u62B1\u6211\u3002</p><p>\u53EF\u6211\u8BA4\u4E3A\u2014\u2014\u6211\u4EEC\u662F\u5728\u6218\u6597\u554A\uFF0CFighting\u6653\u4E0D\u6653\u5F97\uFF1F\u62B1\u4F60\u6211\u4E0D\u5C31\u59A5\u534F\u4E86\u3002 \u4E0D\u62B1\uFF01</p><p>\u7ED3\u679C\u5979\u5C31\u4F1A\u8D8A\u54ED\u8D8A\u51F6\uFF0C\u53C8\u7740\u6025\u53C8\u751F\u6C14\u5730\u8BF4\uFF0C\u4F60\u62B1\u62B1\u6211\uFF0C\u4F60\u62B1\u62B1\u6211\uFF0C\u6211\u5C31\u597D\u4E86\u3002</p><p>\u4E00\u542C\u5979\u8BF4\u8FD9\u53E5\u6211\u5C31\u5FC3\u8F6F\uFF0C\u62B1\u5979\u4E00\u4E0B\uFF0C\u5947\u8FF9\u771F\u7684\u53D1\u751F\u4E86\uFF0C\u4E24\u4E2A\u4EBA\u90FD\u5F00\u59CB\u6162\u6162\u51B7\u9759\u3002</p><p>\u51B7\u9759\u4E0B\u6765\u6211\u518D\u7EC6\u60F3\uFF1A</p><p>\u6709\u7684\u65F6\u5019\u5979\u6CA1\u4EC0\u4E48\u5927\u9519\uFF0C\u662F\u6211\u5FC3\u60C5\u4E0D\u597D\uFF1B</p><p>\u6709\u7684\u65F6\u5019\u786E\u5B9E\u9519\u4E86\uFF0C\u4F46\u5176\u5B9E\u4E0D\u662F\u6545\u610F\u7684\uFF1B</p><p>\u6709\u7684\u65F6\u5019\u5979\u786E\u5B9E\u662F\u6545\u610F\u7684\uFF0C\u4F46\u662F\u5728\u4E24\u8D25\u4FF1\u4F24\u7684\u6B47\u65AF\u5E95\u91CC\u4E2D\uFF0C\u8BB2\u5F97\u4EFB\u4F55\u9053\u7406\u4E5F\u6CA1\u8D77\u4EFB\u4F55\u4F5C\u7528\u3002</p><p>\u901A\u5E38\u7B49\u5979\u5E73\u9759\u4E4B\u540E\uFF0C\u6211\u518D\u8BB2\u9053\u7406\uFF0C\u5979\u5927\u90FD\u80FD\u542C\u8FDB\u53BB\uFF1B\u5BF9\u6211\u7684\u60E9\u6212\u65B9\u6848\uFF0C\u5927\u90FD\u4E5F\u80FD\u63A5\u53D7\u3002</p><p>\u56E0\u4E3A\u521A\u624D\u7684\u62E5\u62B1\u8BA9\u5979\u77E5\u9053\uFF0C\u867D\u7136\u5979\u505A\u9519\u4E86\uFF0C\u4F46\u6211\u4F9D\u7136\u662F\u7231\u5979\u7684\uFF0C\u6240\u4EE5\u5979\u624D\u613F\u610F\u5BF9\u6211\u7ACB\u7684\u89C4\u77E9\u548C\u5904\u7F5A\u591A\u4E00\u5206\u7406\u89E3\u548C\u5C0A\u91CD\u3002</p><p>\u6211\u8FD9\u624D\u660E\u767D\uFF0C\u539F\u6765\u51B2\u7A81\u53D1\u751F\u7684\u65F6\u5019\uFF0C\u6BD4\u6025\u7740\u6559\u80B2\u5B69\u5B50\u66F4\u91CD\u8981\u7684\uFF0C\u662F\u5148\u5E73\u590D\u5F7C\u6B64\u7684\u60C5\u7EEA\uFF0C\u51B7\u9759\u4E0B\u6765\u518D\u8C08\u4E8B\u513F\u3002</p><p>\u5B69\u5B50\u54ED\u95F9\u65E0\u975E\u5C31\u662F\u8981\u83B7\u53D6\u5173\u6CE8\u3001\u53D1\u6CC4\u4E0D\u6EE1\u3001\u9700\u8981\u5B89\u6170\u3002</p><p>\u4E00\u4E2A\u7B80\u5355\u7684\u62E5\u62B1\uFF0C\u5C31\u53EF\u4EE5\u628A\u7236\u6BCD\u7684\u5173\u6CE8\u3001\u5B89\u6170\u3001\u7406\u89E3\u3001\u539F\u8C05\uFF0C\u90FD\u4F20\u9012\u7ED9\u5B69\u5B50\u3002</p><p>\u5B69\u5B50\u7684\u60C5\u7EEA\u9700\u6C42\u5F97\u5230\u6EE1\u8DB3\uFF0C\u4ED6\u624D\u80FD\u6162\u6162\u5E73\u9759\u4E0B\u6765\u3002</p><p>\u76F8\u53CD\uFF0C\u5982\u679C\u5BF9\u4E8E\u5B9D\u5B9D\u53D1\u51FA\u7684\u60C5\u611F\u9700\u6C42\u8BAF\u53F7\uFF0C\u5E38\u5E38\u4E0D\u80FD\u505A\u51FA\u53CA\u65F6\u56DE\u5E94\uFF0C\u90A3\u4E48\u5B9D\u5B9D\u9010\u6E10\u5C31\u4F1A\u840C\u53D1\u51FA\u4E00\u79CD\u5931\u671B\u4E0E\u4E0D\u4FE1\u4EFB\u7684\u60C5\u7EEA\uFF0C\u8FD9\u79CD\u632B\u6298\u611F\uFF0C\u4F1A\u8BA9\u4ED6\u53D8\u5F97\u5BB9\u6613\u9000\u7F29\u3002</p><p>\u5F97\u77E5\u4E86\u5B9D\u5B9D\u6C42\u62E5\u62B1\u540E\u7684\u771F\u76F8\uFF0C\u4F60\u4E0D\u662F\u4E5F\u6709\u70B9\u540E\u6094\u62B1\u4ED6\u6709\u70B9\u5C11\u4E86\uFF1F</p><p>\u65E0\u8BBA\u662F\u7537\u5B69\u5973\u5B69\u90FD\u9700\u8981\u88AB\u62E5\u62B1\uFF0C\u5E38\u88AB\u62E5\u62B1\u7684\u7537\u5B69\u5B50\u4E0D\u4EC5\u4E0D\u4F1A\u53D8\u201C\u5A18\u201D\uFF0C\u53CD\u800C\u4F1A\u4E09\u89C2\u8D85\u6B63\uFF01</p><p>\u56E0\u4E3A\u7236\u6BCD\u5BF9\u5B69\u5B50\u7684\u7231\u8D8A\u6EE1\uFF0C\u4ED6\u7684\u81EA\u5C0A\u5FC3\u4E0E\u6027\u522B\u8BA4\u540C\u624D\u4F1A\u8D8A\u5065\u5168\u3002</p><p>\u540C\u6837\uFF0C\u7238\u7238\u7684\u62E5\u62B1\u4E5F\u548C\u5988\u5988\u7684\u4E00\u6837\u91CD\u8981\u3002</p><p>\u5C24\u5176\u662F\u5BF9\u4E8E\u5973\u5B9D\u5B9D\uFF0C\u4EB2\u5BC6\u5EA6\u662F\u5F00\u542F\u5B89\u5168\u611F\u7684\u9600\u95E8\uFF0C\u7236\u4EB2\u7684\u62E5\u62B1\u624D\u80FD\u8BA9\u5979\u4EEC\u4E0D\u81EA\u5351\u4E5F\u4E0D\u7F3A\u7231\u3002</p><p>\u4EFB\u4F55\u8BED\u8A00\uFF0C\u90FD\u654C\u4E0D\u8FC7\u4E00\u53E5\u201C\u6211\u7231\u4F60\u201D\uFF1B\u4E00\u5343\u53E5\u201C\u6211\u7231\u4F60\u201D\uFF0C\u4E5F\u654C\u4E0D\u8FC7\u201C\u62B1\u62B1\u4F60\u201D\u3002</p>',63);function r(_,c){return a}var n=p(t,[["render",r],["__file","\u62B1\u62B1.html.vue"]]);export{n as default};
